import{_ as e,o as d,c as a,a as l}from"./app.089648b3.js";const P=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"SPA和MPA的区别","slug":"spa和mpa的区别","link":"#spa和mpa的区别","children":[]},{"level":2,"title":"SPA","slug":"spa","link":"#spa","children":[{"level":3,"title":"优点","slug":"优点","link":"#优点","children":[]},{"level":3,"title":"缺点","slug":"缺点","link":"#缺点","children":[]}]},{"level":2,"title":"MPA","slug":"mpa","link":"#mpa","children":[{"level":3,"title":"优点","slug":"优点-1","link":"#优点-1","children":[]},{"level":3,"title":"缺点","slug":"缺点-1","link":"#缺点-1","children":[]}]},{"level":2,"title":"不同之处","slug":"不同之处","link":"#不同之处","children":[]}],"relativePath":"interview/Vue/SPA和MPA的区别.md"}'),i={name:"interview/Vue/SPA和MPA的区别.md"},c=l('<h2 id="spa和mpa的区别" tabindex="-1">SPA和MPA的区别 <a class="header-anchor" href="#spa和mpa的区别" aria-hidden="true">#</a></h2><h2 id="spa" tabindex="-1">SPA <a class="header-anchor" href="#spa" aria-hidden="true">#</a></h2><p><code>SPA</code>全称<code>single page application</code>，它只实际上只加载了一个页面，在页面中采用路由机制实现HTML内容的转换，达到页面切换的效果，减少了页面的重新加载。</p><h3 id="优点" tabindex="-1">优点 <a class="header-anchor" href="#优点" aria-hidden="true">#</a></h3><ul><li>用户体验相比传统的MPA要好很多，速度快，内容变化也不用重新加载整个页面。</li><li>对服务器的压力也比较少</li><li>前后端分离，前端只负责交互逻辑，后端负责数据处理</li></ul><h3 id="缺点" tabindex="-1">缺点 <a class="header-anchor" href="#缺点" aria-hidden="true">#</a></h3><ul><li>首次加载耗时长，通常会在<code>index.html</code>先追加一个<code>loading</code>效果，防止白屏</li><li><code>SEO</code>难度比较大，无法被搜索引擎获取到数据</li></ul><h2 id="mpa" tabindex="-1">MPA <a class="header-anchor" href="#mpa" aria-hidden="true">#</a></h2><p><code>MPA</code>全称<code>Multi page application</code>，在每一次页面跳转的时候都会重新加载一个完整的<code>html</code>页面</p><h3 id="优点-1" tabindex="-1">优点 <a class="header-anchor" href="#优点-1" aria-hidden="true">#</a></h3><ul><li>首屏加载速度快</li><li><code>SEO</code>优化较好，可以被搜索引擎识别出来</li></ul><h3 id="缺点-1" tabindex="-1">缺点 <a class="header-anchor" href="#缺点-1" aria-hidden="true">#</a></h3><ul><li>切换页面时体验没有<code>SPA</code>好</li></ul><h2 id="不同之处" tabindex="-1">不同之处 <a class="header-anchor" href="#不同之处" aria-hidden="true">#</a></h2><ul><li>构成不一样，<code>MPA</code>是多个页面的，<code>SPA</code>是一个页面，多个<code>DOM</code>构成的</li><li>跳转方式不同，<code>MPA</code>是一个页面跳转到另外一个页面，<code>SPA</code>是假跳转，通过操作<code>DOM</code>的方式实现</li><li>URL模式不同，<code>SPA</code>是完整的<code>URL</code>模式，<code>SPA</code>是通过<code>/path/</code>方式模拟</li><li>用户体验不同，<code>SPA</code>在移动设备上体验更好</li><li><code>SEO</code>优化，<code>MPA</code>可以直接处理，而<code>MPA</code>需要额外方案处理</li><li>转场动画实现，<code>MPA</code>无法实现，<code>SPA</code>可以实现</li></ul>',15),o=[c];function t(r,n,h,s,p,u){return d(),a("div",null,o)}const _=e(i,[["render",t]]);export{P as __pageData,_ as default};
